import React from 'react';
import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import { Route } from 'react-router-dom';
import CollectionPage from '../collection/collection.component';
import { connect } from 'react-redux';
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.action';
import { createStructuredSelector } from 'reselect';
import { seclectIscollectionFetching } from '../../redux/shop/shop.selector';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

    componentDidMount({ fetchCollectionsStartAsync }) {
fetchCollectionsStartAsync()
    }
    render() {
        const { match, loading } = this.props;
        return (
            <div className='shop-page'>
                <Route exact
                    path={`${match.path}`}
                    render={(props) => <CollectionOverviewWithSpinner isLoading={loading}{...props} />} />
                <Route
                    path={`${match.path}/:collectionId`}
                    render={(props) => <CollectionWithSpinner isLoading={loading}{...props} />} />
            </div>
        );

    }
}

const mapStateToProps = createStructuredSelector({
    loading: seclectIscollectionFetching
});
const mapdispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => (dispatch(fetchCollectionsStartAsync()))
});


export default connect(mapStateToProps, mapdispatchToProps)(ShopPage);
