import React from 'react';
import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import { Route } from 'react-router-dom';
import CollectionPage from '../collection/collection.component';

import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { selectIsCollectionFetching, selectIscollectionLoaded } from '../../redux/shop/shop.selector';
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.action';

import WithSpinner from '../../components/with-spinner/with-spinner.component';

import CollectionOverviewContainer from '../../components/collections-overview/collection-overview.container';

const CollectionOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionWithSpinner = WithSpinner(CollectionPage);


class ShopPage extends React.Component {

    componentDidMount() {
        const { fetchCollectionsStartAsync } = this.props;
        fetchCollectionsStartAsync();
    }

    render() {
        const { match, loading, isCollectionLoaded } = this.props;
        return (
            <div className='shop-page'>
                <Route exact
                    path={`${match.path}`}
                    component={CollectionOverviewContainer}
                />
                <Route
                    path={`${match.path}/:collectionId`}
                    render={(props) => <CollectionWithSpinner isLoading={!isCollectionLoaded}{...props} />} />
            </div>
        );

    }
}

const mapStateToProps = createStructuredSelector({
    loading: selectIsCollectionFetching,
    isCollectionLoaded: selectIscollectionLoaded
});

const mapdispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});


export default connect(mapStateToProps, mapdispatchToProps)(ShopPage);
